const measurablePath = require('point-at-length');

const paths = {
	'10' : 'M15,25 c 5.5191502449,0 10,-4.4808497551 10,-10',
	'20' : 'M40,25 c 5.5191502449,0 10,-4.4808497551 10,-10 s-4.4808497551,-10 -10,-10',
	'30' : 'M65,25 c 5.5191502449,0 10,-4.4808497551 10,-10 s-4.4808497551,-10 -10,-10 s-10,4.4808497551 -10,10',
	'40' : 'M90,25 c 5.5191502449,0 10,-4.4808497551 10,-10 s-4.4808497551,-10 -10,-10 s-10,4.4808497551 -10,10 s4.4808497551,10  10,10 z'
};

console.log('<?xml version="1.0" encoding="utf-8"?>');
console.log('<svg xmlns="http://www.w3.org/2000/svg" width="840px" height="480px" viewBox="0 0 105 60">');
console.log('<style>');
console.log('path, polyline { fill:none; stroke:black; stroke-width:0.5px }');
console.log('polyline { stroke:red }');
console.log('</style>');
for (var steps in paths) {
	var pathData = paths[steps];
	var path = measurablePath(pathData);
	var len = path.length();
	var pts = [];
	for (var i=0;i<steps;++i) pts.push(path.at(len*i/steps));
	console.log('<path d="'+pathData+'"/>');
	console.log('<polyline points="'+pts.join()+'" transform="translate(0,25)"/>');
}
console.log('</svg>');
